commands.commandLine:
username=${workspace.namespace}
projectname=coolstore${username#user}
oc login ${OPENSHIFT_CONSOLE_URL} -u ${username} -p openshift --insecure-skip-tls-verify
oc project ${projectname}
echo "-----------"
echo "Successful Connected to OpenShift as ${username}"
echo "-----------"

username=${workspace.namespace}
projectname=coolstore${username#user}
infraname=infra${username#user}
echo "Execute in Terminal Windows" && \
echo "squashctl --machine --namespace ${projectname} --debugger java --squash-namespace ${infraname}"

username=${workspace.namespace}
projectname=infra${username#user}
echo "squashctl utils delete-planks --squash-namespace ${projectname}"
squashctl utils delete-planks --squash-namespace ${projectname}
